export default function (state = [ {id: 1, bookTitle: 'The great wall', cover:'asdcx', author: 'qazwsxl'}], action) {
  switch (action.type) {
    case 'BOOK_SELECTED':
      console.log("reducer", state);
      return action.payload;

    case 'ADD_BOOK':
      return [
        ...state,
        Object.assign({}, action.contact)
      ];
      
    case 'DELETE_BOOK':
      console.log("delete bookid", action.id, "state", state);
      let result = state.filter((book) => book.id !== action.id);
      console.log(result);
      return result;
    default:
      return state;
  }
};

