import React, { Component } from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import * as bookAction from '../../actions/index';
import {SearchBook} from '../../actions/index';

class SearchBookComponent extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            searchvalue:''  
         }
         this.handleChange = this.handleChange.bind(this);

    }
    handleChange(e){
        this.setState({
            searchvalue:e.target.value
          });
          console.log("search value in componenet",this.state.searchvalue)
          this.props.SearchBook(this.state.searchvalue);
    }
    render() { 
        return ( <div className="searchBar">
            <input className="form-control" onChange={(e)=> this.props.SearchBook(e.target.value)} />
             
                 {/* <input type="text" value={this.state.searchvalue} onChange={(e)=> bookAction.SearchBook(e.target.value)}/> */}
                     <button>Search</button>
      </div> );
    }
}
const mapStateToProps = (state, ownProps) => {    
    return {        
        books:state.books.books
    }
}
const matchDispatchToProps = (dispatch) => {
    return  bindActionCreators({SearchBook}, dispatch);
    // {        
    //   //  SearchBook: book =>dispatch(bookAction.SearchBook(book)),
    // }
  };
  export default connect(mapStateToProps,matchDispatchToProps)(SearchBookComponent);
