import {BookRecord} from "../API/index.js";
export const fetchBooks = () => (dispatch) => {
    let records = BookRecord;
     console.log("getrecord",records)
    return dispatch({
        type: 'FETCH_BOOKS',
        payload: records
      })
  }

  export const fetchBookRecord = () => {
    return dispatch => {
      dispatch({
        type: 'FETCH_BOOK_BEGIN'
      })
      let records = BookRecord;
    console.log("FETCH_BOOK_SUCCESS",)

      console.log("getrecord",records)
      return setTimeout(() => {
        dispatch({
          type: 'FETCH_BOOK_SUCCESS',
          payload: records
        })
      }, 3000)
    }
  }  

  export function fetchProducts() {
    return dispatch => {
      dispatch(fetchProductsBegin());
      return fetch("/products")
        .then(handleErrors)
        .then(res => res.json())
        .then(json => {
          dispatch(fetchProductsSuccess(json.products));
          return json.products;
        })
        .catch(error => dispatch(fetchProductsFailure(error)));
    };
  }
  function handleErrors(response) {
    if (!response.ok) {
      throw Error(response.statusText);
    }
    return response;
  }
  export const fetchProductsBegin = () => ({
    type: 'FETCH_PRODUCTS_BEGIN'
  });
  
  export const fetchProductsSuccess = products => ({
    type: 'FETCH_PRODUCTS_SUCCESS',
    payload: { products }
  });
  
  export const fetchProductsFailure = error => ({
    type: 'FETCH_PRODUCTS_FAILURE',
    payload: { error }
  });


export const addBook=(book)=>{
    console.log("res", book)
    return {
        type: 'ADD_BOOK',
        payload: book
    }
}
export const UpdateBook=(book)=>{
    return {
        type: 'UPDATE_BOOK',
        payload: book
    }
}
export const DeleteBook= (id) => {
    console.log("delete id",id)
    return {
        type: 'DELETE_BOOK',
        id: id
    }   
}
export const EditBook= (id) => {
    console.log("EditBook id",id)

    return {
        type: 'EDIT_BOOK',
        id: id
    }   
}
export const SearchBookTitle= (booktitle) => {    
    return {
        type: 'SEARCH_BOOK',
        payload: booktitle
    }   
}
export const selectBook = (book) => {  
    return {
        type: 'BOOK_SELECTED',
        payload: book
    }
};
export const checkExistingBook = (book) => {
    return {
        type: 'BOOKTITLE_EXIST',
        payload: book.bookTitle
    }
};
export const DeleteAuthorBooks= (name) => {
    console.log("delete id",name)
    return {
        type: 'DELETE_AUTHOR_BOOKS',
        payload: name
    }   
}
export const DeletePublisherBooks= (name) => {
    console.log("delete id",name)
    return {
        type: 'DELETE_PUBLISHER_BOOKS',
        payload: name
    }   
}
