export default function (state ={books:[]}, action) {
  switch (action.type) {
    case 'BOOK_SELECTED':
      console.log("reducer", state);
      return action.payload;

    case 'ADD_BOOK':
      return [
        ...state,
        Object.assign({}, action.contact)
      ];
      
    case 'DELETE_BOOK':
      console.log("delete bookid", action.id, "state", state);
      return { books: state.books.filter(book =>
        book.id !== action.id
     )}
      //let result = state.filter((book) => book.id !== action.id);
      
      //return result;
    default:
      return state;
  }
};

