import BookRecord from "../API/index";

export const getRecord=()=>{
    let records = BookRecord();
    console.log("getrecord",records)
    return (dispatch, getState) => {       
                dispatch({
                    type : 'FETCH_BOOK_RECORD',
                    payload : records
                });
            
            ;
    }
}
export const addBook=(book)=>{
    console.log("res", book)
    return {
        type: 'ADD_BOOK',
        payload: book
    }
}
export const UpdateBook=(book)=>{
    return {
        type: 'UPDATE_BOOK',
        payload: book
    }
}
export const DeleteBook= (id) => {
    return {
        type: 'DELETE_BOOK',
        id: id
    }   
}
export const EditBook= (id) => {
    return {
        type: 'EDIT_BOOK',
        id: id
    }   
}
export const SearchBook= (booktitle) => {
    return {
        type: 'SEARCH_BOOK',
        payload: booktitle
    }   
}
export const selectBook = (book) => {
    return {
        type: 'BOOK_SELECTED',
        payload: book
    }
};
export const checkExistingBook = (book) => {
    return {
        type: 'BOOKTITLE_EXIST',
        payload: book.bookTitle
    }
};
