const defaultState = {
  books: [],
  loading: false,
  errors:{}
}

export default function (state = defaultState, action) {
  switch (action.type) { 
    case 'FETCH_USERS_REQUEST': {      
    console.log("Reducers FETCH_USERS_REQUEST",action.payload)
      return state.set('loading', true);
    }

    case 'FETCH_USERS_SUCCESS': {      
    console.log("Reducers FETCH_USERS_SUCCESS",action.payload)
      return {
        ...state,
        loading: false,
        books: action.payload
      };
    }
    
    // case 'RECEIVE_POSTS':
    // console.log("Reducers",action.payload)
    // return {      
    //   ...state,
    //  books: action.payload
    // }
    // case 'FETCH_BOOK_RECORD':
    // console.log("reducer ",action.payload)
    // return {
    //   ...state,
    //  books: action.payload
    // } 
    case 'ADD_BOOK':
      return {
        ...state,
        books: [...state.books, action.payload],
        errors: {},
        loading: false
      }
      // console.log("reducer ",action.payload)
      // return {...state,
      //   books:state.concat([action.payload])
      // }
   
    case 'DELETE_BOOK':
      return state.books.filter((book) => book.id !== action.id);
      case 'EDIT_BOOK':
      return state.books.map((book) => book.id === action.id ? { ...book, editing: !book.editing } : book);  
    case 'UPDATE_BOOK':
      return state.books.map((book) => {
        if (book.id === action.payload.id) {
          return {
            ...book,
            booktitle: action.payload.booktitle,
            cover: action.payload.cover,
            author: action.payload.author,
            publisher: action.payload.publisher,
            date: action.payload.date,
            editing: !book.editing
          }
        } else return book;
      })
    // case 'BOOKTITLE_EXIST':
    //   let res = state.some(function (book) { return book['booktitle'] === action.payload })
    //   console.log("res", res)
    //   return res;
    default:
      return state;
  }
};
