import React from 'react';
import {DeleteBook, EditBook } from '../../actions/index';
const DataRowItem=props=>{  
  const {book,dispatch}=this.props;
  
  console.log("DataRowItem Component ",book)
    return (                            
            <tr key={book.id}>
                {/* <td>{this.props.books.length}</td> */}
                <td>{book.booktitle}</td>
                <td>{book.cover}</td>
                <td>{book.author}</td>
                <td>{book.publisher}</td>       
                <td>  <button onClick={(e) =>  dispatch(EditBook(book.id))}>Edit</button><button onClick={(e) =>this.props.dispatch(DeleteBook(book.id))} className="btn btn-danger">Delete</button></td>
            </tr>
        
        
    )
}

export default DataRowItem;
