import React, { Component } from 'react';
import {connect} from 'react-redux';
import * as bookAction from '../../actions/index';
class SearchBook extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            searchvalue:''  
         }
    }
    handleChange(e){
        this.setState({
            searchvalue:e.target.value,
          })
          this.props.SearchBook(this.state.searchvalue);
    }
    render() { 
        return ( <div className="searchBar">
                 <input type="text" onChange={this.handleChange.bind(this)} value={this.state.searchvalue}/>
                     <button>Search</button>
      </div> );
    }
}
const mapStateToProps = (state, ownProps) => {    
    return {        
        books:state.books.books
    }
}
const matchDispatchToProps = (dispatch) => {
    return {        
        SearchBook: book =>dispatch(bookAction.SearchBook(book)),
    }
  };
  export default connect(mapStateToProps,matchDispatchToProps)(SearchBook);
