const Data=({getbooks,onDeleteClick,onEditClick,isediting,Id})=>{
    return (               
        getbooks.map((book) => {             
            return ( <DataItem book={book}
                 key={book.id}
                  onEditClick={()=>onEditClick(book.id)} 
                  onDeleteClick={()=>onDeleteClick(book.id)}
                  isediting={isediting}
                  Id={Id}/>);
        })        
    )
}

export default Data;
