export default function (state = null, action)  {
    switch (action.type){
        case 'ADD_BOOK':
        return [
          ...state,
          Object.assign({}, action.contact)
        ];
        case 'DELETE_BOOK':
        console.log("delete bookid",action.id,"state", state);
         let result=state.filter((book) => book.id !== action.id);
         console.log(result);
         return result;
        default:
              return state;
      }
    };

