const Data=({getbooks,onDeleteClick,onUpdateClick})=>{
    return (               
        getbooks.map((book) => {             
            return ( <DataItem book={book}
                 key={book.id} 
                  onDeleteClick={()=>onDeleteClick(book.id)}
                  onUpdateClick={()=>onUpdateClick()}
                  />);
        })        
    )
}

export default Data;
