export const addBook=(book)=>{
    console.log("book",book);
    return {
        type: 'ADD_BOOK',
        payload: book
    }
}
export const UpdateBook=(book)=>{
    console.log("book",book);
    return {
        type: 'UPDATE_BOOK',
        payload: book
    }
}
export const DeleteBook= (id) => {
    console.log("book",id);
    return {
        type: 'DELETE_BOOK',
        id: id
    }   
}

export const selectBook = (book) => {
    console.log("You clicked on user: ",book.bookTitle);
    return {
        type: 'BOOK_SELECTED',
        payload: book
    }
};
