export default function (state = [], action) {
  switch (action.type) {
    case 'BOOK_SELECTED':
      return action.payload;
    case 'ADD_BOOK':
    console.log(state);
    if(state.length>=1){
       let res = state.some(function (book) { return book['booktitle'] === action.payload.booktitle })
       console.log("res", res)
       if(res)
      return state.concat([action.payload]);
      else return state;
    }
    else{
      return state.concat([action.payload]);
    }
        // return [
        //   ...state,
        //   Object.assign({}, action.payload)
        // ];
    case 'DELETE_BOOK':
      console.log("delete bookid", action.id, "state", state);
      let deleteresult = state.filter((book) => book.id !== action.id);
      console.log(deleteresult);
      return deleteresult;
    case 'UPDATE_BOOK':
      console.log("update bookid", action.payload, "state", state);
      return state.map((book) => {
        if (book.id === action.payload.id) {
          console.log("matchedid");
          return {
            ...book,
            booktitle: action.payload.booktitle,
            cover: action.payload.cover,
            author: action.payload.author,
            publisher: action.payload.publisher,
            date: action.payload.date,
            editing: !book.editing
          }
        } else return book;
      })
    case 'EDIT_BOOK':
      return state.map((book) => book.id === action.id ? { ...book, editing: !book.editing } : book)
    // case 'BOOKTITLE_EXIST':
    //   let res = state.some(function (book) { return book['booktitle'] === action.payload })
    //   console.log("res", res)
    //   return res;
    default:
      return state;
  }
};

