import React, { Component } from 'react';
import {bindActionCreators} from 'redux';
class BooksRecord extends Component {
   booklist(){
    return this.props.books.map((book)=>{
        return (
            <tr
                key={book.id}
                // onClick={() => this.props.selectUser(book)}
            > 
            <td>  {book.id}</td>
              <td>  {book.bookTitle}</td>
              <td>  {book.cover}</td>
              <td>  {book.author}</td>
              <td>  {book.bookTitle}</td>
              <td>  <button>Update</button><button>Delete</button></td>
            </tr>
        );
    })
   }
    render() { 
        return ( <div>
            <table className="BookRecord">
                <thead>
                    <tr>
                        <td>Sr No</td>
                        <td>Book Title</td>
                        <td>Author</td>
                        <td>Publisher</td>
                        <td>Date</td>
                        <td>Action</td>
                    </tr>
                </thead>
                <tbody>
                {this.booklist()}
                </tbody>
            </table>
        </div> );
    }
}
function mapStateToProps(state) {
    return {
        books: state.books
    };
}
export default connect(mapStateToProps)(BooksRecord);
