import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import  DataState  from './Data';
import  LoadingState from './loading';
import ErrorState from './Error';
import BlankState from './BlankState';
import { fetchBooks, DeleteBook, EditBook }from '../../actions/index'; 
class BooksRecord extends Component {
    componentDidMount() {
        this.props.dispatch(fetchBooks());
      }
    render() {
        const { isError, books,isFetching } = this.props;
        // if (isError) {
        //     return <ErrorState/>;
        // }
        return (<div>
                {isError && (<ErrorState/>)}
                {isFetching && books.length === 0 && <LoadingState/>}
                {!isFetching && books.length === 0 && <BlankState/>}
                {books.length>0 &&(<DataState books={books}/>)}
            
            </div>);
    }
}
const mapStateToProps = state => ({
    books: state.books.items,
    loading: state.books.loading,
    isError: state.books.error,
    isFetching:state.books.Fetchbook
  });
  BooksRecord.propTypes = {
    selectedSubreddit: PropTypes.string.isRequired,
    posts: PropTypes.array.isRequired,
    isFetching: PropTypes.bool.isRequired,
    lastUpdated: PropTypes.number,
    dispatch: PropTypes.func.isRequired
  }
 export default connect(mapStateToProps)(BooksRecord);
