import React, { Component } from 'react';
import { connect } from 'react-redux';
import EditBook from '../EditBook/EditBookComponent';
import BlankState from './BlankState';
import * as bookAction from '../../actions/index'
import { fetchBooks }from '../../actions/index'; 
import { bindActionCreators } from 'redux';
class Data extends Component {
    constructor(props) {
        super(props);
        this.state = {}
    }
    
    deleteBook(e, id) {
        e.preventDefault();
        this.props.DeleteBook(id);
    }
    editBook(e, id) {
        e.preventDefault();       
        this.props.EditBook(id);
    }
    // componentDidMount() {
    //     bookAction.getRecord();
    //  }
    // componentDidMount() {
    //      console.log("mountcomponent",fetchBooks())
    //     // this.props.dispatch(fetchBooks());
    //     fetchBooks();
    //   }
    // componentDidMount() {
    //     this.props.dispatch(fetchBooks);
    //   }
    render() {
        
        console.log("cehek",this.props.books)
        
        if (this.props.books.length > 0) {
            
            return (
                
                this.props.books.map((book) => {
                    if(book.editing===true){
                        // eslint-disable-next-line no-unused-expressions
                       return <EditBook book={book} key={book.id}/>
                    } else {
                    return (<tr
                        key={book.id}
                    >
                        {/* <td>{this.props.books.length}</td> */}
                        <td>{book.booktitle}</td>
                        <td>{book.cover}</td>
                        <td>{book.author}</td>
                        <td>{book.publisher}</td>
                        <td>  <button onClick={(e) => this.editBook(e, book.id)}>Edit</button><button onClick={(e) => this.deleteBook(e, book.id)} className="btn btn-danger">Delete</button></td>
                        
                    </tr>);}
                })
                
            )
        }
        else return <BlankState />
    }
}

const mapStateToProps = (state, ownProps) => {
    
    console.log("map",state);
    return {
        books:state.books.books
    }
}

const matchDispatchToProps = (dispatch) => {
    return {
         DeleteBook: index => dispatch(bookAction.DeleteBook(index)),
         EditBook: index => dispatch(bookAction.EditBook(index)),fetchBooks
        
        // DeleteBook: () => {
        //     dispatch(bookAction.DeleteBook)
        //   },
        //   EditBook: () => {
        //     dispatch(bookAction.EditBook)
        //   },
       // getRecord: dispatch(bookAction.getRecord())
      //  getRecord:index =>dispatch(bookAction.getRecord(index))
        
    }
};
// const mapDispatchToProps = (dispatch) => {
//     return bindActionCreators({
//         DeleteBook:bookAction.DeleteBook,
//         EditBook:bookAction.EditBook
//     },dispatch);
//     }
export default connect(mapStateToProps, (matchDispatchToProps))(Data);
