import React, { Component } from 'react';
import './App.css';
import BooksRecord from './components/AllBooks/AllBookComponent';
import AddBook from './components/AddBook/AddBookComponent';
import SearchBook from './components/SearchBook';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Author from './pages/author/author'
import Publisher from './pages/publisher/publisher'
class App extends Component { 
  render() {
    return (
    <Router><div className="Body">
      <header>        
        <Link to={'/'}><h1>Book Management App</h1></Link>
      </header>
      <div className="container">
        <div className="leftnav">
        <Link to={'/Publisher'} className="nav-link">Publisher</Link>
        <Link to={'/Author'} className="nav-link">Author</Link>

          <button>Author</button>
        </div>
        <div className="rightnav">
          <button>Reset</button>
        </div>        
        <SearchBook/>
        <BooksRecord />
        <AddBook/>        
      </div>
    </div>
     <Switch>
     {/* <Route exact path='/index' component={ Home } /> */}
     <Route path='/Author' component={ Author } />
     <Route path='/Publisher' component={ Publisher } />
 </Switch>
 </Router>
    );
  }
}

export default App;
