import React, { Component } from 'react';
import { connect } from 'react-redux';
import { UpdateBook }  from '../../actions/index';

class EditBook extends Component {
    constructor(props) {
        super(props);    
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event,value) {
        event.preventDefault();
        let book = {
            id: this.props.book.id,
            booktitle: this.booktitle.value,
            cover: this.cover.value,
            author: this.authorname.value,
            publisher: this.publishername.value,            
        }
        console.log(value)
        //this.props.enableEditFlag(value);
        console.log(book)
        this.props.onUpdateClick(book);        
    }
    render() {
       
         return (
            <tr
                key={this.props.book.id}>                
                <td>
                    <input type="text" ref={(input)=>this.bookname=input} required />
                </td>
                <td>
                    <input type="text"  ref={(input)=>this.cover=input} required />
                </td>
                <td>
                    <input type="text"  ref={(input)=>this.authorname=input} required />
                </td>
                <td>
                    <input type="text"  ref={(input)=>this.publishername=input}  />
                </td>
                <td>  <button onClick={(e) => this.handleSubmit(e, false)}>Update</button></td>
            </tr>
        );
    }
}


export default connect()(EditBook);
