import React, { Component } from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {UpdateBook,DeleteBook} from '../actions/index';


import * as bookAction from '../actions/index';
class BooksRecord extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          booktitle:'',
          cover:'',
          authorname:'',
          publishername:''
         }
         
    }
       
    handleChangeBookTitle(e){
        this.setState({
          booktitle:e.target.value
        })
      }
      handleChangeCover(e){
        this.setState({
          cover:e.target.value,
        })
      }
      handleChangeAuthor(e){
        this.setState({
          authorname:e.target.value,
        })
      }
      handleChangePublisher(e){
        this.setState({
          publishername:e.target.value,
        })
      }
      handleChangeDate(e){
        this.setState({
          date:e.target.value
        })
      }
      UpdateBook(event) {
          event.preventDefault();
          let book={
            booktitle:this.state.booktitle,
            cover:this.state.cover,
            author:this.state.authorname,
            publisher:this.state.publishername,
          //  date:this.state.date
          }
         //console.log(book);
          // this.setState({
          //   booktitle:'',
          //   cover:'',
          //   authorname:'',
          //   publishername:'',
          //   date:''
          // });
          this.props.UpdateBook(book);
        }



   booklist(){
       if(this.props.books.length>0){
    return this.props.books.map((book)=>{
        return (
            <tr
                key={book.id}
                //onClick={() => this.props.selectBook(book)}
            > 
            <td>{this.props.books.length}</td>
              <td> <input type="text" onChange={this.handleChangeBookTitle.bind(this)} value= {book.booktitle}/></td>
              <td> <input type="text" onChange={this.handleChangeCover.bind(this)} value=  {book.cover}/></td>
              <td> <input type="text" onChange={this.handleChangeAuthor.bind(this)} value=  {book.author}/></td>
              <td> <input type="text" onChange={this.handleChangePublisher.bind(this)} value=  {book.publisher}/></td>
              <td>  <button onClick={(e) => this.UpdateBook(e,book.id)}>Update</button><button  onClick={(e) => this.deleteContact(e, book.id)} className="btn btn-danger">Delete</button></td>
            </tr>
        );
    })
}
else{
    return <tr>
        <td><h4>No Record Exist
          </h4>
            </td></tr>;
}       
   }
   deleteContact(e, id){
    e.preventDefault();
    this.props.DeleteBook(id);
  }
    render() { 
        return ( <div>
            <table className="BookRecord">
                <thead>
                    <tr>
                        <td>Sr No</td>
                        <td>Book Title</td>
                        <td>Cover</td>
                        <td>Author</td>
                        <td>Publisher</td>
                        <td>Action</td>
                    </tr>
                </thead>
                <tbody>
                {this.booklist()}
                </tbody>
            </table>
        </div> );
    }
}
const mapStateToProps = (state, ownProps) => {
    return {
        books: state.bookReducer
    }
  };
// function mapStateToProps(state) {
//     return {
//         books: state.books
//     };
// }
// function matchDispatchToProps(dispatch){
//     return bindActionCreators({UpdateBook: UpdateBook,DeleteBook:DeleteBook}, dispatch);
//      //  { createContact: contact => dispatch(contactAction.createContact(contact)) }
   
    
// }
const matchDispatchToProps = (dispatch) => {
    return {        
      DeleteBook: index =>dispatch(bookAction.DeleteBook(index)),
      UpdateBook:index =>dispatch(bookAction.UpdateBook(index)),      
    //  EditBook:index =>dispatch(bookAction.EditBook(index)),
    //  selectBook:book=>dispatch(bookAction.selectBook(book))
     // bindActionCreators({DeleteBook: DeleteBook,selectBook:selectBook}, dispatch) 
    }
  };
export default connect(mapStateToProps,matchDispatchToProps)(BooksRecord);
