import React, { Component } from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import {UpdateBook,DeleteBook} from '../actions/index';


import * as bookAction from '../actions/index';
class BooksRecord extends Component {
   booklist(){
    return this.props.books.map((book)=>{
        return (
            <tr
                key={book.id}
                //onClick={() => this.props.selectBook(book)}
            > 
            <td>{this.props.books.length}</td>
              <td>  {book.booktitle}</td>
              <td>  {book.cover}</td>
              <td>  {book.author}</td>
              <td>  {book.publisher}</td>
              <td>  <button onClick={() => this.props.EditBook(book.id)}>Edit</button><button  onClick={(e) => this.deleteContact(e, book.id)} className="btn btn-danger">Delete</button></td>
            </tr>
        );
    })
   }
   deleteContact(e, id){
    e.preventDefault();
    this.props.DeleteBook(id);
  }
    render() { 
        return ( <div>
            <table className="BookRecord">
                <thead>
                    <tr>
                        <td>Sr No</td>
                        <td>Book Title</td>
                        <td>Cover</td>
                        <td>Author</td>
                        <td>Publisher</td>
                        <td>Action</td>
                    </tr>
                </thead>
                <tbody>
                {this.booklist()}
                </tbody>
            </table>
        </div> );
    }
}
const mapStateToProps = (state, ownProps) => {
    return {
        books: state.bookReducer
    }
  };
// function mapStateToProps(state) {
//     return {
//         books: state.books
//     };
// }
// function matchDispatchToProps(dispatch){
//     return bindActionCreators({UpdateBook: UpdateBook,DeleteBook:DeleteBook}, dispatch);
//      //  { createContact: contact => dispatch(contactAction.createContact(contact)) }
   
    
// }
const matchDispatchToProps = (dispatch) => {
    return {        
      DeleteBook: index =>dispatch(bookAction.DeleteBook(index)),
      UpdateBook:index =>dispatch(bookAction.UpdateBook(index)),      
      EditBook:index =>dispatch(bookAction.EditBook(index)),
    //  selectBook:book=>dispatch(bookAction.selectBook(book))
     // bindActionCreators({DeleteBook: DeleteBook,selectBook:selectBook}, dispatch) 
    }
  };
export default connect(mapStateToProps,matchDispatchToProps)(BooksRecord);
