import React from 'react';
import EditBookState from '../EditBook/EditBookComponent';

let isEditing = false;
const DataRowItem = ({ book, onDeleteClick, onEditClick,Id }) => {
    console.log("DataRowItem Component ", book)
    return (
        // isediting && <EditBookState />
        <tbody>
        {isEditing && (book.id===Id) && <EditBookState book={book}/>}
        {(book.id!==Id) && (  <tr key={book.id}>
            {/* <td>{this.props.books.length}</td> */}
            <td>{book.booktitle}</td>
            <td>{book.cover}</td>
            <td>{book.author}</td>
            <td>{book.publisher}</td>
            <td> <button onClick={() => isEditing = !isEditing} className="btn btn-primary">Edit</button>
                <button onClick={() => onDeleteClick()} className="btn btn-danger">Delete</button>
            </td>
        </tr>
        )}
        </tbody>
    )
}

export default DataRowItem;
