const defaultState = {
  books: [],
  loading: false,
  errors:{}
}

export default function (state = defaultState, action) {
  switch (action.type) {    
    case 'ADD_BOOK':
      // return {
      //   ...state,
      //   contacts: [...state.contacts, action.payload],
      //   errors: {},
      //   loading: false
      // }
      return {...state,
        books:state.concat([action.payload])
      }
    case 'FETCH_BOOK_RECORD':
    console.log("reducer ",action.payload)
    return {
      ...state,
     books: action.payload
    }
    case 'DELETE_BOOK':
      return state.filter((book) => book.id !== action.id);
      case 'EDIT_BOOK':
      return state.map((book) => book.id === action.id ? { ...book, editing: !book.editing } : book);  
    case 'UPDATE_BOOK':
      return state.map((book) => {
        if (book.id === action.payload.id) {
          return {
            ...book,
            booktitle: action.payload.booktitle,
            cover: action.payload.cover,
            author: action.payload.author,
            publisher: action.payload.publisher,
            date: action.payload.date,
            editing: !book.editing
          }
        } else return book;
      })
    // case 'BOOKTITLE_EXIST':
    //   let res = state.some(function (book) { return book['booktitle'] === action.payload })
    //   console.log("res", res)
    //   return res;
    default:
      return state;
  }
};
