import React, { Component } from 'react';
import { connect } from 'react-redux';
import BlankState from './BlankState';
import * as bookAction from '../../actions/index'

class Data extends Component {
    constructor(props) {
        super(props);
        this.state = {}
    }
    deleteBook(e, id) {
        e.preventDefault();
        this.props.DeleteBook(id);
    }   
    render() {
        if (this.props.books.length > 0) {
            
            return (
                this.props.books.map((book) => {                    
                    return (<tr
                        key={book.id}
                    >
                        <td>{this.props.books.length}</td>
                        <td>{book.booktitle}</td>
                        <td>{book.cover}</td>
                        <td>{book.author}</td>
                        <td>{book.publisher}</td>
                        <td><button onClick={(e) => this.deleteBook(e, book.id)} className="btn btn-danger">Delete</button></td>
                    </tr>);
                })
            )
        }
        else return <BlankState />
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        books:state.books.books
    }
}
const matchDispatchToProps = (dispatch) => {
    return {
        DeleteBook: index => dispatch(bookAction.DeleteBook(index)),
    }
};
export default connect(mapStateToProps, matchDispatchToProps)(Data);
