const defaultState = {
  books: [],
  loading: false,
  errors:{},
  filteredbook:[],
  searchvalue:false,

}
const _ = require('lodash')

export default function (state = defaultState, action) {
  switch (action.type) { 
   case 'FETCH_BOOKS': {      
    console.log("Reducers FETCH_BOOKS",action.payload)
      return {
        ...state,
        loading: false,
        books: action.payload
      };
    }
  
    // case 'FETCH_BOOK_RECORD':
    // console.log("reducer ",action.payload)
    // return {
    //   ...state,
    //  books: action.payload
    // } 
    case 'ADD_BOOK':
      return {
        ...state,
        books: [...state.books, action.payload],
        errors: {},
        loading: false
      }
      // console.log("reducer ",action.payload)
      // return {...state,
      //   books:state.concat([action.payload])
      // }
   
    case 'DELETE_BOOK':
    console.log("reducer ",action.id)
      return {...state,
       books: state.books.filter((book) => book.id !== action.id),
       filteredbook: state.filteredbook.filter((book) => book.id !== action.id),

      }
      case 'EDIT_BOOK':
      return {
        ...state,
      books:  state.books.map((book) => book.id === action.id ? { ...book, editing: !book.editing } : book),
      filteredbook:  state.filteredbook.map((book) => book.id === action.id ? { ...book, editing: !book.editing } : book)
      
      }
    case 'UPDATE_BOOK':
      return{
        ...state,
        books:
        state.books.map((book) => {
        if (book.id === action.payload.id) {
          return {
            ...book,
            booktitle: action.payload.booktitle,
            cover: action.payload.cover,
            author: action.payload.author,
            publisher: action.payload.publisher,
            date: action.payload.date,
            editing: !book.editing
          }
        } else return book;
      }),
      filteredbook:state.filteredbook.map((book) => {
        if (book.id === action.payload.id) {
          return {
            ...book,
            booktitle: action.payload.booktitle,
            cover: action.payload.cover,
            author: action.payload.author,
            publisher: action.payload.publisher,
            date: action.payload.date,
            editing: !book.editing
          }
        } else return book;
      })
    }
    case 'SEARCH_BOOK':  
    const {payload} = action
   console.log("payload",payload);
   let filtereddata=[];
   let searchflag=false;
   if(payload!=='')   
   {
     filtereddata = _.filter(state.books, (o) => _.toLower(o.booktitle).includes(_.toLower(payload)));
     searchflag=true;
   }
  //  const filtered =  state.books.filter(book =>  book.booktitle.indexOf(payload)!==-1)
   console.log("filtered",filtereddata);
    return {...state,
      filteredbook:filtereddata,
      searchvalue:searchflag
    }
  //  return{
  //    ...state,
  //   books:  state.books.filter(book =>  book.booktitle.indexOf(action.payload)!==-1)
      
  //    }
     case 'DELETE_AUTHOR_BOOKS':
     console.log("reducer ",action.payload)
       return {...state,
        books: state.books.filter((book) => book.author !== action.payload)
       }
       case 'DELETE_PUBLISHER_BOOKS':
     console.log("reducer ",action.payload)
       return {...state,
        books: state.books.filter((book) => book.publisher !== action.payload)
       }
    // case 'BOOKTITLE_EXIST':
    //   let res = state.some(function (book) { return book['booktitle'] === action.payload })
    //   console.log("res", res)
    //   return res;
    default:
      return state;
  }
};
