export default  (state = [], action) => {
    switch (action.type){
        case 'ADD_BOOK':
        return [
          ...state,
          Object.assign({}, action.contact)
        ];
        case 'DELETE_BOOK':
        console.log("delete",action.id);
        return state.filter((state, i) => i !== action.id);
        default:
              return state;
      }
    };

