import React, { Component } from 'react';
import { connect } from 'react-redux';
import { UpdateBook }  from '../../actions/index';

class EditBook extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.book.id,
            booktitle: this.props.book.booktitle,
            cover: this.props.book.cover,
            authorname: this.props.book.author,
            publishername: this.props.book.publisher,
            date: this.props.book.date
        }
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChangeBookTitle(e) {
        this.setState({
            booktitle: e.target.value
        })
    }
    handleChangeCover(e) {
        this.setState({
            cover: e.target.value,
        })
    }
    handleChangeAuthor(e) {
        this.setState({
            authorname: e.target.value,
        })
    }
    handleChangePublisher(e) {
        this.setState({
            publishername: e.target.value,
        })
    }
    handleChangeDate(e) {
        this.setState({
            date: e.target.value
        })
    }
    handleSubmit(event) {
        event.preventDefault();
        let book = {
            id: this.props.book.id,
            booktitle: this.state.booktitle,
            cover: this.state.cover,
            author: this.state.authorname,
            publisher: this.state.publishername,
            date: this.state.date
        }
        this.props.enableEditFlag=true;
        console.log(book)
        this.props.onUpdateClick(book);
    }
    render() {
         return (
            <tr
                key={this.state.id}>                
                <td>
                    <input type="text" onChange={this.handleChangeBookTitle.bind(this)} id="bookname" value={this.state.booktitle} required />
                </td>
                <td>
                    <input type="text" onChange={this.handleChangeCover.bind(this)} id="covername" value={this.state.cover} required />
                </td>
                <td>
                    <input type="text" onChange={this.handleChangeAuthor.bind(this)} id="authorname" value={this.state.authorname} required />
                </td>
                <td>
                    <input type="text" onChange={this.handleChangePublisher.bind(this)} id="publishername" value={this.state.publishername} required />
                </td>
                <td>  <button onClick={(e, val) => this.handleSubmit(e, val)}>Update</button></td>
            </tr>
        );
    }
}


export default connect()(EditBook);
