import React, { Component } from 'react';
class AddBook extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          booktitle:'',
          cover:'',
          authorname:'',
          publishername:'',
          date:''
         }
         
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);   
    }
    handleChange(e){
      this.setState({
        booktitle:e.target.value,
        cover:e.target.value,
        authorname:e.target.value,
        publishername:e.target.value,
        date:e.target.value
      })
    }
    handleSubmit(event) {
        event.preventDefault();
        let book={
          booktitle:this.state.booktitle,
          cover:this.state.cover,
          author:this.state.authorname,
          publisher:this.state.publishername,
          date:this.state.date
        }
        console.log(book);
        this.setState({
          booktitle:'',
          cover:'',
          authorname:'',
          publishername:'',
          date:''
        });
      }
    render() { 
        return ( 
            <div className="modal fade" id="myModal" role="dialog">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <button type="button" className="close" data-dismiss="modal">&times;</button>
                <h4 className="modal-title">Add new Record</h4>
              </div>
              <div className="modal-body">
              <form  onSubmit={(val) => this.handleSubmit(val)}>
                <input type="text" hidden id="bookid" />
                <label>Book Name</label>
                <input type="text" onChange={this.handleChange} id="bookname" value={this.state.booktitle}/>                
                <label>Cover</label>
                <input type="text"onChange={this.handleChange} id="covername" value={this.state.cover}/>
                <label>Author</label>
                <input type="text" onChange={this.handleChange} id="authorname" value={this.state.authorname}/>
                <label>Publisher</label>
                <input type="text" onChange={this.handleChange} id="publishername" value={this.state.publishername}/>
                <label>Date</label>
                <input type="date" onChange={this.handleChange} id="date" value={this.state.date}/>
                
                <button className='submit'>Add!</button>
        </form>
                {/* <button type="button" className="btn btn-default" id="savebutton" onClick={() => this.props.addBook(book)}>Save</button> */}
              </div>
            </div>
          </div>
        </div>
         );
    }
}
 
export default AddBook;
