import React, { Component } from 'react';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import * as bookAction from '../../actions/index';
import {SearchValue} from '../../actions/index';

class SearchBook extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            searchvalue:''  
         }
    }
    // handleChange(e){
    //     this.setState({
    //         searchvalue:e.target.value
    //       });
    //       console.log("search value in componenet",this.state.searchvalue)
    //       this.props.SearchBook(this.state.searchvalue);
    // }
    render() { 
        return ( <div className="searchBar">
            <input className="form-control" onChange={(e)=> this.props.SearchValue(e.target.value)} />
             
                 {/* <input type="text" value={this.state.searchvalue} onChange={(e)=> bookAction.SearchBook(e.target.value)}/> */}
                     <button>Search</button>
      </div> );
    }
}
const mapStateToProps = (state, ownProps) => {    
    return {        
        books:state.books.books
    }
}
const matchDispatchToProps = (dispatch) => {
    return  bindActionCreators({SearchValue}, dispatch);
    // {        
    //   //  SearchBook: book =>dispatch(bookAction.SearchBook(book)),
    // }
  };
  export default connect(mapStateToProps,matchDispatchToProps)(SearchBook);
