import React, { Component } from 'react';
import { connect } from 'react-redux';

import * as bookAction from '../actions/index';
class EditBook extends Component {
    constructor(props) {
        super(props);
        this.state = {             
            id:this.props.book.id,
          booktitle:this.props.book.booktitle,
          cover:this.props.book.cover,
          authorname:this.props.book.author,
          publishername:this.props.book.publisher,
          date:this.props.book.date
         }
         
    this.handleSubmit = this.handleSubmit.bind(this);   
    }
    handleChangeBookTitle(e){
      this.setState({
        booktitle:e.target.value
      })
    }
    handleChangeCover(e){
      this.setState({
        cover:e.target.value,
      })
    }
    handleChangeAuthor(e){
      this.setState({
        authorname:e.target.value,
      })
    }
    handleChangePublisher(e){
      this.setState({
        publishername:e.target.value,
      })
    }
    handleChangeDate(e){
      this.setState({
        date:e.target.value
      })
    }
    handleSubmit(event) {
        event.preventDefault();
        let book={
            id:this.props.book.id,
          booktitle:this.state.booktitle,
          cover:this.state.cover,
          author:this.state.authorname,
          publisher:this.state.publishername,
          date:this.state.date
        }
       //console.log(book);
        // this.setState({
        //   booktitle:'',
        //   cover:'',
        //   authorname:'',
        //   publishername:'',
        //   date:''
        // });
        this.props.UpdateBook(book);
      }
    render() { 
        console.log("check")
        return ( 
            <tr
            key={this.state.id}
            //onClick={() => this.props.selectBook(book)}
        > <td></td>
        <td>
        <input type="text" onChange={this.handleChangeBookTitle.bind(this)} id="bookname" value={this.state.booktitle} required/>
       </td>
          <td>
        <input type="text"onChange={this.handleChangeCover.bind(this)} id="covername" value={this.state.cover} required/>
        </td>
          <td>
           <input type="text" onChange={this.handleChangeAuthor.bind(this)} id="authorname" value={this.state.authorname} required/>
          </td>
          <td>
               <input type="text" onChange={this.handleChangePublisher.bind(this)} id="publishername" value={this.state.publishername} required/>
          </td>
          <td>  <button onClick={(e,val) => this.handleSubmit(val)}>Update</button></td>
        </tr> 
    //      <div className="modal fade" id="myModal" role="dialog">
    //     <div className="modal-dialog">
    //       <div className="modal-content">
    //         <div className="modal-header">
    //           <button type="button" className="close" data-dismiss="modal">&times;</button>
    //           <h4 className="modal-title">Edit Record</h4>
    //         </div>
    //         <div className="modal-body">
    //         <form  onSubmit={(val) => this.handleSubmit(val)}>
    //           <input type="text" hidden id="bookid" value={this.props.book.id}/>
    //           <label>Book Name</label>
    //           <input type="text" onChange={this.handleChangeBookTitle.bind(this)} id="bookname" value={this.state.booktitle} required/>                
    //           <label>Cover</label>
    //           <input type="text"onChange={this.handleChangeCover.bind(this)} id="covername" value={this.state.cover} required/>
    //           <label>Author</label>
    //           <input type="text" onChange={this.handleChangeAuthor.bind(this)} id="authorname" value={this.state.authorname} required/>
    //           <label>Publisher</label>
    //           <input type="text" onChange={this.handleChangePublisher.bind(this)} id="publishername" value={this.state.publishername} required/>
    //           <label>Date</label>
    //           <input type="date" onChange={this.handleChangeDate.bind(this)} id="date" value={this.state.date} required/>
              
    //           <button className='submit'>Update!</button>
    //   </form>
    //           {/* <button type="button" className="btn btn-default" id="savebutton" onClick={() => this.props.addBook(book)}>Save</button> */}
    //         </div>
    //       </div>
    //     </div>
    //   </div> 
    );
    }
}
 
const matchDispatchToProps = (dispatch) => {
    return {       
      UpdateBook:index =>dispatch(bookAction.UpdateBook(index)),
    }
  };
  export default connect(null,matchDispatchToProps)(EditBook);
