import React, { Component } from 'react';
import { connect } from 'react-redux';
import BlankState from './BlankState';
import * as bookAction from '../../actions/index'

class Data extends Component {
    constructor(props) {
        super(props);
        this.state = {}
    }
    deleteBook(e, id) {
        e.preventDefault();
        this.props.DeleteBook(id);
    }   
    render() {
        const record=this.props.books;
        const result={};
        record.forEach(function(i) { result[record[i].author] = (result[record[i].author]||0) + 1;});
        // for (let i = 0; i < record.length; i++) {           
        //         result[record[i].author] = (result[record[i].author] || 0) + 1;   
        // }
    console.log("count",result);
    const list= Object.keys(result).map(key => ({ "Name":key,"numberofBook": result[key] }));    
        console.log(list)
        if (record.length > 0) {
            
            return (
                record.map((book) => {                    
                    return (<tr
                        key={book.id}
                    >
                        <td>{book.booktitle}</td>
                        <td>{book.cover}</td>
                        <td>{book.author}</td>
                        <td>{book.publisher}</td>
                        <td><button onClick={(e) => this.deleteBook(e, book.id)} className="btn btn-danger">Delete</button></td>
                    </tr>);
                })
            )
        }
        else return <BlankState />
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        books:state.books.books
    }
}
const matchDispatchToProps = (dispatch) => {
    return {
        DeleteBook: index => dispatch(bookAction.DeleteBook(index)),
    }
};
export default connect(mapStateToProps, matchDispatchToProps)(Data);
