const initialState=[
  {id: 1, bookTitle: 'The great wall', cover:'asdcx', author: 'qazwsxl'},
{id: 2, bookTitle: 'oop', cover:'asdcx', author: 'qazwsxl'},
{id: 3, bookTitle: 'datastructure', cover:'asdcx', author: 'qazwsxl'},
{id: 4, bookTitle: 'operating system', cover:'asdcx', author: 'qazwsxall'},
{id: 5, bookTitle: 'the explorers club', cover:'asdcx', author: 'qazwsxuch'},
{id: 6, bookTitle: 'maths',cover:'asdcx', author: 'qazwsx5'},
{id: 7, bookTitle: 'english',cover:'asdcx', author: 'qazwsx'}]

export default function (state = initialState, action) {
  switch (action.type) {
    case 'BOOK_SELECTED':
      console.log("reducer", state);
      return action.payload;

    case 'ADD_BOOK':
      return [
        ...state,
        Object.assign({}, action.contact)
      ];
      
    case 'DELETE_BOOK':
      console.log("delete bookid", action.id, "state", state);
      let result = state.filter((book) => book.id !== action.id);
      console.log(result);
      return result;
    default:
      return state;
  }
};

