import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import DataState from './Data';
import LoadingState from './loading';
import ErrorState from './Error';
import BlankState from './BlankState';
import EditBookState from '../EditBook/loading';
import SearchBook from '../SearchBook/Searchcomponent';


import {fetchBookRecord, fetchBooks, DeleteBook, EditBook,UpdateBook } from '../../actions/index';
class BooksRecord extends Component {
    constructor(props) {
        super(props);  
        this.state={
            filteredlist:this.props.books,
            searchflag:false
        } 
        this.DeleteBook = this.DeleteBook.bind(this)
      }
    componentDidMount() {
        this.props.dispatch(fetchBookRecord());
    }
    DeleteBook(id) {
        console.log("delete",id)
       // this.props.dispatch(DeleteBook(id));
    }
    UpdateBook(book){
        console.log("updatebook",book)
        this.props.dispatch(UpdateBook(book));
    }
    handleSearch(val){
        console.log('search value',val);
        const _ = require('lodash');
        this.setState({filteredlist: _.filter(this.props.books, (o) => _.toLower(o.booktitle).includes(_.toLower(val))),
                           searchflag:val===null?false:true                 
        });
        console.log("check:",this.state.filteredlist);
    }
    render() {
        const { books, isFetching } = this.props;        
        const searchbooks=  this.state.filteredlist.length===0 &&this.state.searchflag===true? books:this.state.filteredlist;
        
        console.log("Render",searchbooks);
        console.log("Main Book Component error,isfetch,items",isFetching,books)  
        // if (isError) {
        //     return <ErrorState/>;
        // }
        return (<div>     
            <SearchBook  allBooks={books}  onHandleSearch={(id)=>{this.handleSearch(id)}}/>       
            {/* {isError && (<ErrorState />)} */}
            {isFetching && books.length === 0 && <LoadingState />}
            {!isFetching && books.length === 0 && <BlankState />}
            { books.length > 0 && (
                 <table className="BookRecord">
                 <thead>
                     <tr>
                         {/* <td>Sr No</td> */}
                         <td>Book Title</td>
                         <td>Cover</td>
                         <td>Author</td>
                         <td>Publisher</td>
                         <td>Action</td>
                     </tr>
                 </thead>
                 <DataState getbooks={searchbooks}                  
                  onDeleteClick={(id)=>{this.DeleteBook(id)}}
                onUpdateClick={(book)=>{this.UpdateBook(book)}}               
                     />
             </table>
               
                )
            }
            {/* {isediting && <EditBookState/>} */}

        </div>);
    }
}
const mapStateToProps = state => ({
    books: state.books.items,
    isFetching: state.books.isfetch
});
BooksRecord.propTypes = {
    books: PropTypes.array.isRequired,
    isFetching: PropTypes.bool.isRequired,
}
export default connect(mapStateToProps)(BooksRecord);
